const responseData = hopp.response.body.asJSON();

const ACCESS_TOKEN_VAR = "access_token";
const REFRESH_TOKEN_VAR = "refresh_token";

function extractAndSetTokens(data) {
    let tokenFound = false;

    // 1. Kiểm tra và lưu ACCESS TOKEN
    if (data && data.access_token) {
        hopp.env.active.set(ACCESS_TOKEN_VAR, data.access_token);
        console.log(`Access Token đã được lưu: ${data.access_token.substring(0, 30)}...`);
        tokenFound = true;
    }

    // 2. Kiểm tra và lưu REFRESH TOKEN (nếu có)
    if (data && data.refresh_token) {
        hopp.env.active.set(REFRESH_TOKEN_VAR, data.refresh_token);
        console.log(`Refresh Token đã được lưu: ${data.refresh_token.substring(0, 30)}...`);
        tokenFound = true;
    }

    return tokenFound;
}


// A. Trích xuất từ responseData trực tiếp (ví dụ: {"access_token": "...", "refresh_token": "..."})
let found = extractAndSetTokens(responseData);

// B. Trích xuất từ đối tượng 'data' lồng ghép (ví dụ: {"data": {"access_token": "...", "refresh_token": "..."}})
if (!found && responseData && responseData.data) {
    found = extractAndSetTokens(responseData.data);
}

if (found) {
    // Tùy chọn: Thêm test để xác nhận response thành công (Status 2xx)
    hopp.test("Login successful (Status 2xx)", () => {
        hopp.expect(hopp.response.statusCode).toBeLevel2xx();
    });
}
else {
    console.error("Không tìm thấy 'access_token' hoặc 'refresh_token' trong response body.");
}

